<?php
 require_once(dirname(__FILE__) . '/tag.php'); require_once(dirname(__FILE__) . '/encoding.php'); class SimpleByName { var $_name; function SimpleByName($name) { $this->_name = $name; } function isMatch($widget) { return ($widget->getName() == $this->_name); } } class SimpleByLabel { var $_label; function SimpleByLabel($label) { $this->_label = $label; } function isMatch($widget) { if (! method_exists($widget, 'isLabel')) { return false; } return $widget->isLabel($this->_label); } } class SimpleById { var $_id; function SimpleById($id) { $this->_id = $id; } function isMatch($widget) { return $widget->isId($this->_id); } } class SimpleByLabelOrName { var $_label; function SimpleByLabelOrName($label) { $this->_label = $label; } function isMatch($widget) { if (method_exists($widget, 'isLabel')) { if ($widget->isLabel($this->_label)) { return true; } } return ($widget->getName() == $this->_label); } } ?>